import React, {useEffect, useState} from "react";
import { GoogleLogin } from '@react-oauth/google';
import jwt_decode from "jwt-decode";
import { BsFillPlusCircleFill } from "react-icons/bs";
import Modal from 'react-modal';

//https://www.npmjs.com/package/@react-oauth/google
//https://www.npmjs.com/package/jwt-decode

const customStyles = {
    content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
},
};


export const Home = () => {
    const [credentials, setCredentials] = useState([])
    
    const decodeJWT = () => {
        let cred = window.sessionStorage.getItem("credential");
        if(cred !== null) {
            var decoded = jwt_decode(cred);
            setCredentials(decoded)
            // console.log(cred);
        }
    }

    useEffect(() => {
        decodeJWT()
    }, [])

    const logout = () => {
        sessionStorage.removeItem('credential');
        window.location.reload();
    }

    return(<>
        {credentials.email ?<>
            <div className="homeContainer">
                <div>
                    {/* <h1>logged in</h1> */}
                    <BsFillPlusCircleFill size={25} className="taskButton"/>
                </div>

                
                <button onClick={()=>logout()} className="logInOutButton">Logout</button>
            </div>
        </>:
            <div className="loginContainer">
                <h1>Please login</h1>
                <div className="logInOutButton">
                    <GoogleLogin
                    onSuccess={credentialResponse => {
                        console.log(credentialResponse)
                        window.sessionStorage.setItem("credential", credentialResponse.credential);
                        decodeJWT()
                    }}
                    onError={() => {
                        console.log('Login Failed')
                    }}
                    />
                </div>
            </div>
        }
    </>)
}
