import React from "react";

export const Task = (props) => {
    // console.log(props)
    const convertDate = (date) => {
        let year = date.substring(0,4)
        let tempMonth = date.substring(5,7)
        let day = date.substring(8,10)
        let month = ''

        if(tempMonth === "01") {
            month = 'January'
        } else if(tempMonth === "02") {
            month = 'February'
        } else if(tempMonth === "03") {
            month = 'March'
        } else if(tempMonth === "04") {
            month = 'April'
        } else if(tempMonth === "05") {
            month = 'May'
        } else if(tempMonth === "06") {
            month = 'June'
        } else if(tempMonth === "07") {
            month = 'July'
        } else if(tempMonth === "08") {
            month = 'August'
        } else if(tempMonth === "09") {
            month = 'September'
        } else if(tempMonth === "10") {
            month = 'October'
        } else if(tempMonth === "11") {
            month = 'November'
        } else if(tempMonth === "12") {
            month = 'December'
        }

        return month + day + ', ' + year
    }

    return(<>
        <div className="taskItem">
            <h4>{props.taskname}</h4>
            <h4>Due: {convertDate(new Date(props.date).toISOString().substring(0, 10))}</h4>
            {/* <button onClick={props.onClick(props.taskname)}>Edit</button> */}
            <button onClick={event => props.handleClick(props.taskname,new Date(props.date))}>Edit</button>
        </div>
    </>)
}
